import NoteContext from "./noteContext"
import { useState } from "react"

const NoteState = (props) =>{
    const s1 = {
        "name":"Nitin",
        "class":"T.Y.Btech"
    }
    const [state, setState] = useState(s1)
    const update=()=>{
        setTimeout(() => {
            setState({
                "name":"Sonu",
                "class":"L.Y.Btech"
            })
        }, 1000);
    }
    return(
        <NoteContext.Provider value={s1,update}>
            {props.children}
        </NoteContext.Provider>
    )
}
export default NoteState;
