// WaterRippleBackground.js
import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';

// Define the ripple animation
const rippleAnimation = keyframes`
  to {
    transform: scale(2);
    opacity: 0;
  }
`;

// Styled component for the background
const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  background-image: url('path/to/your/background-image.jpg');
  background-size: cover;
  overflow: hidden;
`;

// Styled component for the ripple effect
const Ripple = styled.div`
  position: absolute;
  border-radius: 50%;
  background-color: rgba(0, 150, 255, 0.5);
  transform-origin: center;
  animation: ${rippleAnimation} 1s linear;
`;

const WaterRippleBackground = () => {
  const [ripplePosition, setRipplePosition] = useState({ x: 0, y: 0 });

  const handleMouseEnter = (e) => {
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    setRipplePosition({ x, y });
  };

  return (
    <BackgroundContainer onMouseEnter={handleMouseEnter}>
      <Ripple
        style={{
          left: ripplePosition.x,
          top: ripplePosition.y,
        }}
      />
    </BackgroundContainer>
  );
};

export default WaterRippleBackground;
