import React from "react";
import "./nav.css";
import { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Register = (props) => {
  const navigate = useNavigate()
  const [createuser, setcreateuser] = useState({ name :"", email : "", password: "", });
  const handleSubmit = async (e) => {
    e.preventDefault();
    const{name,email,password}=createuser
    try {
      const response = await fetch(
        `http://localhost:5000/api/auth/createuser/`,
        {
          method: "POST",
          headers: {
            "content-type": "application/json",
          },
          body: JSON.stringify({ name, email, password, }),
        }
      );
      const json = await response.json();
      console.log(json);
      localStorage.setItem('token',json.token);
      navigate('/login')
      // props.showAlert("User Created ! Login to continue","success")
    } catch (error) {
      alert("Error in Sign Up");
      console.log(error,": Email or password Exists");
    }
  };
  const onchange = (e) => {
    setcreateuser({ ...createuser, [e.target.name]: e.target.value });
  };

  return (
    <>
      <header className="or">
        <h1 className="text-center">Create your Login</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group my-4 mx-2">
            <label htmlFor="name">
              Name<span className="text-danger">*</span>
            </label>
            <input
              type="text"
              name="name"
              className="form-control my-3"
              defaultValue={createuser.name}
              placeholder="Enter User Name"
              id="name"
              minLength={5}
              onChange={onchange}
            />
          </div>

          <div className="form-group">
            <label htmlFor="email1">
              Email address<span className="text-danger">*</span>
            </label>
            <input
              type="email"
              name="email"
              className="form-control my-3"
              defaultValue={createuser.email}
              placeholder="Enter Email Address"
              id="email"
              onChange={onchange}
              aria-describedby="emailHelp"
              minLength={5}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">
              Password<span className="text-danger">*</span>
            </label>
            <input
              type="password"
              name="password"
              className="form-control my-3"
              defaultValue={createuser.password}
              placeholder="Enter Password"
              id="password"
              onChange={onchange}
              minLength={5}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary my-3">
            Submit
          </button>
          <br />

          <Link to="/login" className="nav-item text-decoration-none">
            Already a user? Login here
          </Link>
        </form>
      </header>
    </>
  );
};

export default Register;
