import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./components/Home";
import About from "./components/About";
import NoteState from "./context/NoteState";
import Login from "./components/Login";
import Register from "./components/Register";
import Alert from "./components/Alert";
import { useState } from "react";
import Footer from "./components/Footer";



function App() {
  const [alert, setAlert] = useState(null);
  const showAlert = (massage, type) => {
    setAlert({
      msg: massage,
      type: type,
    });
    setTimeout(() => {
      setAlert(null);
    }, 5000);
  };
  return (
    <>
      <NoteState showAlert={showAlert}>
        <Router>
          <Navbar title="iNotebook" showAlert={showAlert} />
          <marquee direction="left" className="text-secondary bg-light mt-4 mb-0">
            Thanks for Visiting iNotebook ! Feel free to explore and start creating your notes. Check About page for Information.
          </marquee>
          <Alert alert={alert}/>
          <div className="container">
            <Routes>
              <Route path="/" element={<Home showAlert={showAlert}/>}  />
              <Route path="/About" element={<About showAlert={showAlert}/>} />
              <Route path="/login" element={<Login showAlert={showAlert}/>} />
              <Route path="/signup" element={<Register showAlert={showAlert}/>} />
            </Routes>
          </div>
        </Router>
        <Footer/>
      </NoteState>
    </>
  );
}

export default App;
