import "./nav.css";
import React from "react";
import { useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

const Navbar = (props) => {
  const navigate = useNavigate();
  let location = useLocation();
  const logout = () => {
    localStorage.removeItem("token");
    navigate("/login");
    props.showAlert("Successfully Logout", "success");
  };
  useEffect(() => {
    console.log(location.pathname);
  }, [location]);
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <Link className="navbar-brand text-primary title" to="/">
          {props.title}
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarScroll"
          aria-controls="navbarScroll"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse display" id="navbarScroll">
          <ul className="navbar-nav mr-auto my-2 my-lg-0 navbar-nav-scroll">
            <li className="nav-item ">
              <Link
                className={`nav-link ${
                  location.pathname === "/" ? "active" : ""
                }`}
                to="/"
              >
                Home
              </Link>
            </li>
            <li className="nav-item dropdown">
              <Link
                className={`nav-link ${
                  location.pathname === "/About" ? "active" : ""
                }`}
                to="/About"
                role="button"
              >
                About
              </Link>
            </li>
          </ul>
          <div>
            <form class="form-inline my-2 my-lg-0">
              <input
                class="form-control mr-sm-2"
                type="search"
                placeholder="Search"
                aria-label="Search"
              />
              <button
                class="btn btn-outline-success my-2 my-sm-0"
                type="submit"
              >
                Search
              </button>
            </form>
          </div>
          <div>
            {!localStorage.getItem("token") ? (
              <form className="d-flex form">
                <Link
                  to="/login"
                  className="mx-1 btn b btn-primary btn-lg out"
                  role="button"
                >
                  Login
                </Link>
                <Link
                  to="/signup"
                  className="mx-1 btn b btn-primary btn-lg out"
                  role="button"
                >
                  SignUp
                </Link>
              </form>
            ) : (
              <button
                className="mx-1 btn btn-primary btn-lg out"
                onClick={logout}
                role="button"
              >
                Logout
              </button>
            )}
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
