import "./nav.css"
import React from "react";
import Log_Img from ".//logout.png"
import Login from ".//login.png"
import signup from ".//signup.png"
import { useEffect } from "react";
import { Link ,useLocation, useNavigate } from "react-router-dom";

const Navbar = (props) => {
  const navigate = useNavigate()
    let location = useLocation()
    const logout=()=>{
      localStorage.removeItem('token')
      navigate('/login')
    }
    useEffect(()=>{
        console.log(location.pathname)
    },[location])
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <Link className="navbar-brand text-primary note" to="/">
          {props.title}
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarScroll"
          aria-controls="navbarScroll"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarScroll">
          <ul className="navbar-nav mr-auto my-2 my-lg-0 navbar-nav-scroll">
            <li className="nav-item ">
              <Link className={`nav-link ${location.pathname==="/"?"active":""}`} to="/">
                Home
              </Link>
            </li>
            <li className="nav-item dropdown">
              <Link className={`nav-link ${location.pathname==="/About"?"active":""}`} to="/About" role="button">
                About
              </Link>
            </li>
          </ul>
           { !localStorage.getItem('token')?<form className="d-flex form"><Link to ="/login" className="img b" role="button"><img src={Login} alt="Login" /></Link>
          <Link to ="/signup" className="b img" role="button"><img src={signup} alt="Signup" /></Link> 
          </form>:<button  className="out" onClick={logout} role="button"><img className="img_out" src={Log_Img} alt="Logout" /></button>}
         
        </div>
      </nav>
    </>
  );
};

export default Navbar;
