import React from "react";

const Search = () => {
  const searchInput = getElementById("search-input");
  const searchButton = getElementById("search-button");

  searchButton.addEventListener("click", () => {
    const searchText = searchInput.value.toLowerCase();
    const notes = getElementsByClassName("note");

    for (let i = 0; i < notes.length; i++) {
      const note = notes[i];
      const noteText = note.textContent.toLowerCase();

      if (noteText.includes(searchText)) {
        note.style.display = "block";
      } else {
        note.style.display = "none";
      }
    }
  });
  return (
    <div>
      <input type="text" id="search-input" placeholder="Search notes..." />
      <button id="search-button">Search</button>
    </div>
  );
};

export default Search;
