import React, { useState } from "react";
import NoteContext from "./noteContext";

const Alert = (props) =>{
  const [alert, setAlert] = useState(null);
  const capitalize = (word) => {
    const lower = word.toLowerCase();
    return lower.charAt(0).toUpperCase() + lower.slice(1);
  };
  const caption=(props)=>{
    <div style={{height: '50px'}}>
    {props.alert &&  <div className={`alert alert-${props.alert.type} alert-dismissible fade show`} role="alert" > <strong className="text-success"> {capitalize(props.alert.type)} </strong>: {props.alert.msg} </div>}
    </div>
  }
  const showAlert = (massage, type) => {
    setAlert({
      msg: massage,
      type: type,
    });
    setTimeout(() => {
      setAlert(null);
    }, 5000);
  };
  return (
    <>
    <NoteContext.Provider value={{capitalize ,caption,showAlert}} >
    {props.children} 
    </NoteContext.Provider>
    </>
  );
};

export default Alert;
