import React from "react";
import "./nav.css";
import { useState } from "react";
import { Link } from "react-router-dom";

const Register = (props) => {
  const [createuser, setcreateuser] = useState({
    name: "",
    email: "",
    password: "",
    cpassword:""
  });
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:5000/api/auth/createuser/`,
        {
          method: "POST",
          headers: {
            "content-type": "application/json",
          },
          body: JSON.stringify({
            name: createuser.name,
            email: createuser.email,
            password: createuser.password,
          }),
        }
      );
      const json = await response.json();
      console.log(json);
      // props.showAlert("User Created ! Login to continue","success")
    } catch (error) {
      alert("Error in Sign Up");
      console.log(error, ": Email or password Exists");
    }
  };
  const onchange = (e) => {
    setcreateuser({ ...createuser, [e.target.name]: e.target.value });
  };

  return (
    <>
      <header className="or">
        <h1>Create your Login</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group my-4">
            <label htmlFor="name">
              Name<span className="text-danger">*</span>
            </label>
            <input
              type="text"
              name="name"
              className="form-control my-3"
              defaultValue={createuser.name}
              placeholder="Enter User Name"
              id="name"
              onChange={onchange}
            />
          </div>

          <div className="form-group">
            <label htmlFor="email1">
              Email address<span className="text-danger">*</span>
            </label>
            <input
              type="email"
              name="email"
              className="form-control my-3"
              defaultValue={createuser.email}
              placeholder="Enter Email Address"
              id="exampleInputEmail1"
              onChange={onchange}
              aria-describedby="emailHelp"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">
              Password<span className="text-danger">*</span>
            </label>
            <input
              type="password"
              name="password"
              className="form-control my-3"
              defaultValue={createuser.password}
              placeholder="Enter Password"
              id="password"
              onChange={onchange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">
              Confirm Password<span className="text-danger">*</span>
            </label>
            <input
              type="cpassword"
              name="cpassword"
              className="form-control my-3"
              defaultValue={createuser.password}
              placeholder="Confirm Password"
              id="cpassword"
              onChange={onchange}
            />
          </div>

          <button type="submit" className="btn btn-primary my-3">
            Submit
          </button>
          <br />

          <Link to="/login" className="nav-item text-decoration-none">
            Already a user? Login here
          </Link>
        </form>
      </header>
    </>
  );
};

export default Register;
