import NoteContext from "./noteContext";
import { useState } from "react";
const NoteState = (props) => {
  const host = "http://localhost:5000";
  const notesinitial = [];
  let [notes, setNotes] = useState(notesinitial);

  // Get all Notes
  const getnotes = async () => {
    // API call
    const response = await fetch(`${host}/api/notes/fetchallnotes/`, {
      method: "GET",
      headers: {
        "content-type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjVhOTNjNDI4Y2FiY2ExOTUzZDE2NzZiIn0sImlhdCI6MTcwNTU4OTg1MX0.LGM0aaLY2kp5FMu3SJ4S-VaPrGoKfxEMwZhilG1U1gg",
      },
    });
    const json = await response.json();
    console.log(json);
  };
  // Add Note
  const addnote = async (title, description, tag) => {
    // API call
    const response = await fetch(`${host}/api/notes/addnotes/`, {
      method: "POST",
      headers: {
        "content-type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjVhOTNjNDI4Y2FiY2ExOTUzZDE2NzZiIn0sImlhdCI6MTcwNTU4OTg1MX0.LGM0aaLY2kp5FMu3SJ4S-VaPrGoKfxEMwZhilG1U1gg",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    //  const json = response.json();
    //  Logic
    console.log("Adding a new Note");
    notes = {
      _id: "65ace945efa3cd56802523a73",
      user: "65a93c428cabca1953d1676b",
      title: title,
      description: description,
      tag: tag,
      date: "2024-01-21T09:52:05.956Z",
      __v: 0,
    };
    setNotes(notesinitial.concat(notes));
  };
  // delete Note
  const deletenote = async (id, title, description, tag) => {
    // API call
    const response = await fetch(`${host}/api/notes/deletenote/${id}`, {
      method: "POST",
      headers: {
        "content-type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjVhOTNjNDI4Y2FiY2ExOTUzZDE2NzZiIn0sImlhdCI6MTcwNTU4OTg1MX0.LGM0aaLY2kp5FMu3SJ4S-VaPrGoKfxEMwZhilG1U1gg",
      },
      body: JSON.stringify({ id, title, description, tag }),
    });
    //  const json = response.json();
    // logic
    console.log("Deleting the note with id: " + id);
    const newnotes = notes.filter((notes) => {
      return notes._id !== id;
    });
    setNotes(newnotes);
  };
  // Update Note
  const updatenote = async (id, title, description, tag) => {
    // API call
    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "POST",
      headers: {
        "content-type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjVhOTNjNDI4Y2FiY2ExOTUzZDE2NzZiIn0sImlhdCI6MTcwNTU4OTg1MX0.LGM0aaLY2kp5FMu3SJ4S-VaPrGoKfxEMwZhilG1U1gg",
      },
      body: JSON.stringify({ id, title, description, tag }),
    });
    //  const json = response.json();
    // Logic
    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };
  return (
    <NoteContext.Provider
      value={{ notes, setNotes, addnote, deletenote, updatenote }}
    >
      {props.children}
    </NoteContext.Provider>
  );
};
export default NoteState;
