import React, { useState } from 'react'
import "./nav.css"
import { Link, useNavigate} from 'react-router-dom'

const Login = (props) => {
  const navigate = useNavigate()
  const [credentials, setcredentials] = useState({email:"",password:""})
  // props.showAlert("Login to Your Account","success")
  const handleSubmit =async(e)=>{
    e.preventDefault()
    try {
      const response = await fetch(`http://localhost:5000/api/auth/login/`, {
        method: "POST",
        headers: {
          "content-type": "application/json",
          },
          body: JSON.stringify({ email:credentials.email,password:credentials.password}),
      });
      const json = await response.json()
      console.log(json)
      if(json.success){
        // save the auth token and redirect
        localStorage.setItem('token',json.token);
        navigate('/')
      }
      else{
        // props.showAlert("Invalid Username or Password","Danger")
        alert("Invalid Credentials")
      }
    } catch (error) {
      alert("Error in Login");
      console.log(error, ": Failed to Fetch API");
    }
  }
  const onchange = (e) => {
    setcredentials({ ...credentials, [e.target.name]: e.target.value });
  };
  return (
    <>
    <header className='for'>
      <h1 className='container m-4'>Login to iNotebook</h1>
      <form className='container mx-4 my-5'  onSubmit={handleSubmit}>

          <div className="form-group my-4">
            <label htmlFor="email">Email address<span className='text-danger'>*</span></label>
            <input type="email" name='email' value={credentials.email} onChange={onchange} className="form-control f my-3" placeholder='Enter Your personal email Id' id="email" aria-describedby="emailHelp" />
            <small id="emailHelp" className="form-text text-muted"> We'll never share your email with anyone else. </small>
          </div>

          <div className="form-group my-4">
            <label htmlFor="password">Password<span className='text-danger'>*</span></label>
            <input type="password" name='password' value={credentials.password} onChange={onchange} className="form-control f my-3" placeholder='Enter strong password' id="password" />
          </div>

          <button type="submit" className="btn btn-primary my-2"> Submit </button><br />

          <Link to="/signup" className='nav-item text-decoration-none'>Don't have login? Register here</Link>
        </form>

        </header>
    </>
  )
}

export default Login
